message = message.trim().split(" ")
objects = []

function isWordInArray(word){
    let finded = false
    let index = -1
    objects.forEach((e, i) => {
        if (e.word === word){
            finded = true
            index = i
        }
    })
    return index
}

function plusOne(word){
    objects.forEach((e,i) => {
        if (e.word === word.toLowerCase()){
            objects[i].repeat = objects[i].repeat + 1
        }
    })
}

for (let i=0; i<message.length; i++){
    if (isWordInArray(message[i]) === -1){
        // no encontrado
        objects.push({
            word: message[i].toLowerCase(),
            repeat: 1
        })
    }else{
        // encontrado
        plusOne(message[i])
    }
}

function decryptMessage(objects){
    let message = ""
    objects.forEach(e => {
        message += e.word + e.repeat
    })
    return message
}

console.log(decryptMessage(objects))
